<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Posting extends CI_Controller {    public function __construct()    {        parent::__construct();		$this->data['page_title'] = 'Home';        $this->load->library(array('form_validation','session'));        $this->load->helper(array('url','html','form'));		$this->load->model('model_customer');		$this->load->model('model_groups');		$this->load->model('model_users');     }      public function select()	 {		 	$this->data['district_data'] = $this->model_customer->getDistrict(0); 			$this->data['taluk_data'] = $this->model_customer->getTaluk(0);			$this->data['panchayath_data'] = $this->model_customer->getPanchayath(0);					$this->load->view('select', $this->data);	 }		 	  public function result()	 {        $panchayath_id = $this->input->post('panchayath_id');        $group_id = "12";		$this->data['data'] = $this->model_customer->send_data_two('users','group_id',$group_id,'panchayath_id',$panchayath_id);        $panchayath_id = $this->input->post('panchayath_id');        $group_ids = "13";		$this->data['datas'] = $this->model_customer->send_data_two('users','group_id',$group_ids,'panchayath_id',$panchayath_id);		$this->load->view('result', $this->data);	 }		 public function create()	{		$this->form_validation->set_rules('full_name', 'full_name', 'required');        if ($this->form_validation->run() == TRUE) {            // true case        	$data = array(        		'dist_id' => $this->input->post('dist_id'),        		'taluk_id' => $this->input->post('taluk_id'),        		'panchayath_id' => $this->input->post('panchayath_6'),        		'user_type_id' => $this->input->post('group_id'),        		'full_name' => $this->input->post('full_name'),        		'phone' => $this->input->post('phone'),				'status' => 'Active'        	);			//print_r($data);die;	  $create = $this->db->insert('users_posting', $data);        		redirect('/', 'refresh');        }      	}		public function get_sub_Taluk()	{ 		if( $this->input->post('district') >0 ){					  $cat2_data = $this->model_customer->getTaluk($this->input->post('district')); 		  $return = '<option value="">Select Taluk</option>';             foreach($cat2_data as $val) {   				$return .= '<option value="'.$val['id'].'">'.$val['taluk_name'].'</option>';  		    } 		    echo $return; exit;	    }	}		public function get_sub_President()	{ 		if( $this->input->post('taluk') >0 ){		  $cat2_data = $this->model_customer->getPanchayath($this->input->post('taluk')); 		  $return = '<option value="">Select Panchayath</option>';             foreach($cat2_data as $val) {   				$return .= '<option value="'.$val['id'].'">'.$val['panchayath_name'].'</option>';		    } 		    echo $return; exit;	    }	}}